The following C++ example uses COM_Execute command for communication

            // Unselect this to connect to USB device
            #define PoKeys_USB

            #include "stdafx.h"
            #include <stdio.h>

            // Import TLB file - this must be the same version as the DLL
            #import "..\PoKeysDevice_DLL.tlb"  no_namespace


            int _tmain(int argc, _TCHAR* argv[])
            {
            	BSTR builddate = SysAllocString(L"");

            	printf("---------------------------------");
            	printf("\nTesting PoKeys DEMO device - DLL\n");
            	printf("---------------------------------");

            	try
            	{
            		// Initialize COM 
            		CoInitialize(NULL);

            		long st = 0;
            		unsigned char id = 0;

            		// PoKeys device pointer
            		IPoKeysDevicePtr ptr;
            		// Create instance of the device
            		ptr.CreateInstance(__uuidof(PoKeysDevice));

            		if (ptr == 0)
            		{
            			printf("\nCOM initialization failed - check if TLB has the same version as the DLL");
            			getchar();
            			return 1;
            		}

            		// From here on, PoKeys can simply be used by refering to the ptr

            		long i;
            #ifdef PoKeys_USB
            		//Enumerate devices - USB
            		i = ptr->EnumerateDevices();
            #else
            		// List ethernet devices
            		sPoKeysNetworkDeviceData * netDev = new sPoKeysNetworkDeviceData();
            		ptr->StartEthernetDiscovery();
            		Sleep(1000);
            		ptr->StopEthernetDiscovery();

            		i = ptr->GetNumberOfDetectedNetworkDevices();
            #endif		

            		if (i > 0)
            		{

            #ifdef PoKeys_USB
            			// Connect to the first device - USB
            			ptr->ConnectToDevice(0);
            #else
            			// Get device data
            			ptr->COM_GetNetworkDeviceData((int)netDev, (int)0);
            			ptr->ConnectToNetworkDevice(netDev->IPAddress);
            #endif

            			// Retrieve user ID
            			ptr->GetUserID(&id);
            			// Retrieve build date
            			ptr->GetBuildDate(&builddate);

            			// This call retrieves the current device status (still under development. Additional entries will be available soon)
            			sPoKeysDeviceStatus * devStat = new sPoKeysDeviceStatus();

            			// PoKeys device data structure is filled with the configuration
            			ptr->COM_Execute((int)devStat, (int)(ePoKeysDLLOperations_GetDeviceData));
            			ptr->COM_Execute((int)devStat, (int)(ePoKeysDLLOperations_GetPinConfiguration));
            			ptr->COM_Execute((int)devStat, (int)(ePoKeysDLLOperations_GetPeripheralConfiguration));


            			// 1000x read inputs and encoders (and measure time that it took to execute)
            			DWORD start = GetTickCount();

            			bool result = false;
            			for (int k = 0; k<1000; k++)
            			{
            				result = ptr->COM_Execute((int)devStat,    (int)(ePoKeysDLLOperations_GetDigitalPinValues) );
            /*												 		|| (int)(ePoKeysDLLOperations_GetEncoderValues)
            												 		|| (int)(ePoKeysDLLOperations_GetAnalogPinValues)
            												 		|| (int)(ePoKeysDLLOperations_GetDigitalCounterValues)); */
            				// devStat->PoKeysPins[0].DigitalInputValue		- digital input value
            				// devStat->PoKeysPins[0].AnalogValue			- analog input value
            				// devStat->PoKeysPins[0].DigitalCounterValue	- digital counter input value

            				// devStat->Encoders[0].encoderValue;		    - encoder value

            				// Outputs
            				// devStat->PoKeysPins[0].DigitalOutputValue	- digital output value
            				// ptr->COM_Execute((int)devStat, (int)(ePoKeysDLLOperations_SetDigitalPinValues));
            			}
            			DWORD end = GetTickCount();

            			ptr->SetFastEncodersStatus(10);

            			// Disconnect from device
            			ptr->DisconnectDevice();

            			// Print data
            			printf("\nUser ID: %d", id);
            			wprintf(L"\nBuild date: %s", builddate);
            			printf("\n1000 calls took %d miliseconds", end-start);
            		} else
            		{
            			printf("\nNo PoKeys devices found!");
            		}
            	}
            	catch( char * str ) {
            		printf("Exception raised: %s", str);
            	}

            	// Unitialize COM
            	CoUninitialize();

            	printf("\n\nTest complete");

            	getchar();
            	return 0;
            }

